mapper.select:

SELECT COUNT(*)
FROM p_comment
WHERE articleNO=#{articleNO}

mapper.update:

UPDATE p_board
SET title=#{title}, content=#{content}

  , imageFileName=#{imageFileName}

WHERE articleNO=#{articleNO}

mapper.delete:

DELETE FROM p_board
WHERE articleNO in (
	SELECT articleNO FROM p_board
	START WITH articleNO = #{articleNO}
	CONNECT BY PRIOR articleNO = parentNO
)
